// Helper to render a blank line
func renderBlankLine(text string, blankIndex int) string {
	words := strings.Fields(text)
	if blankIndex < 0 || blankIndex >= len(words) {
		return text
	}
	words[blankIndex] = "____"
	return strings.Join(words, " ")
}

